import "Turbine.UI.Lotro"; -- for font definitions-- returns a default local objectfunction MakeLocale()    local L = {};    setmetatable(L, {__index=function(L, key) return key; end;});    return L;end-- loads the localization strings for the given packagefunction ImportLocale( package, locale )--**v Detect Game Language v**-- Legend: 0 = invalid / 2 = English / 268435457 = EnglishGB / 268435459 = Francais / 268435460 = Deutsch / 268435463 = Russianlocale = Turbine.Engine.GetLanguage();if locale == 0 or locale == 2 or locale == 268435457 then locale = "en";elseif locale == 268435459 then locale = "fr";elseif locale == 268435460 then locale = "de"; elseif locale == 268435463 then locale = "ru"; end    pcall(import, ("%s.locale.%s"):format(package, locale));end